TRUNCATE TABLE ddl_event_log;CREATE OR REPLACE FUNCTION tools.relkind_name (relkind text, out relkind_name text)  RETURNS textAS $BODY$SELECT CASE  WHEN relkind = 'r' THEN 'table'  WHEN relkind = 'i' THEN 'index'  WHEN relkind = 'S' THEN 'sequence'  WHEN relkind = 't' THEN 'TOAST table'  WHEN relkind = 'v' THEN 'view'  WHEN relkind = 'm' THEN 'materialized view'  WHEN relkind = 'c' THEN 'composite type'  WHEN relkind = 'f' THEN 'foreign table'  WHEN relkind = 'p' THEN 'partitioned table'  WHEN relkind = 'I' THEN 'partitioned index'  ELSE 'Unexpected relkind ' || relkindEND;$BODY$	LANGUAGE sql;ALTER FUNCTION tools.relkind_name (relkind text, out relkind_name text)	OWNER TO user_bender;REVOKE INSERT ON record_changes_log_detail FROM group_iceberg;ALTER DEFAULT PRIVILEGES IN SCHEMA api REVOKE ALL PRIVILEGES ON FUNCTIONS FROM PUBLIC;GRANT EXECUTE ON ALL FUNCTIONS IN SCHEMA types_plus TO group_iceberg;ALTER FUNCTION tools.relkind_name (relkind text, out relkind_name text) OWNER TO user_bender;DROP FUNCTION tools.relkind_name (relkind text, out relkind_name text) ;SELECT * FROM ddl_event_log;