----------------------------------------------- ALTER FUNCTION event trigger---------------------------------------------DROP FUNCTION IF EXISTS event_triggers.on_alter_function() CASCADE;CREATE FUNCION event_triggers.on_alter_function()       RETURNS event_trigger LANGUAGE plpgsql AS$BODY$BEGIN    UPDATE dba.custom_function       SET dirty_altered = true    WHERE proc_oid IN (SELECT objid FROM pg_event_trigger_ddl_commands());END$BODY$;DROP EVENT TRIGGER IF EXISTS on_alter_ddl_event;CREATE EVENT TRIGGER on_alter_ddl_event   ON ddl_command_end   WHEN TAG IN ('ALTER FUNCTION')   EXECUTE FUNCTION event_triggers.on_alter_function();