----------------------------------------------- DROP FUNCTION event trigger---------------------------------------------DROP FUNCTION IF EXISTS event_triggers.on_drop_function() CASCADE;CREATE FUNCION event_triggers.on_drop_function()       RETURNS event_trigger LANGUAGE plpgsql AS$BODY$BEGIN    UPDATE dba.custom_function       SET dirty_dropped = true    WHERE proc_oid IN (SELECT objid FROM pg_event_trigger_dropped_objects());END$BODY$;DROP EVENT TRIGGER IF EXISTS on_drop_ddl_event;CREATE EVENT TRIGGER on_drop_ddl_event   ON sql_drop   WHEN TAG IN ('DROP FUNCTION')   EXECUTE FUNCTION event_triggers.on_drop_function();