---------------------------------------- Define the dictionary--------------------------------------CREATE TEXT SEARCH DICTIONARY ascendco.simple_skip_dictionary (    TEMPLATE = pg_catalog.simple,    STOPWORDS = english);---------------------------------------- Create a text search configuration--------------------------------------CREATE TEXT SEARCH CONFIGURATION ascendco.simple_skip (COPY = simple);ALTER  TEXT SEARCH CONFIGURATION ascendco.simple_skip   ALTER MAPPING FOR asciiword WITH ascendco.simple_skip_dictionary;  -- 1, 'Word, all ASCII'