-------------------------- day_name------------------------DROP DOMAIN IF EXISTS domains.day_name;CREATE DOMAIN domains.day_name AS	citext	NOT NULL	CONSTRAINT day_name_legal_values		CHECK(      		VALUE IN (      		    'Sunday',				'Monday',				'Tuesday',				'Wednesday',				'Thursday',				'Friday',				'Saturday'      		)  	 );COMMENT ON DOMAIN domains.day_name IS	'Valid full day names in English.';