-------------------------- user_name------------------------DROP DOMAIN IF EXISTS domains.user_name;CREATE DOMAIN domains.user_name AS	citext	NOT NULL	CONSTRAINT user_name_legal_values		CHECK(      		VALUE IN (      		    'postgres',				'rds_super',				'user_bender',				'user_change_structure',				'user_cleanup',				'user_domo_pull',				'user_iceberg',				'user_iceberg_remote',				'user_leviathan',				'user_reporting',				'user_saws',				'user_sonar'      		)  	 );COMMENT ON DOMAIN domains.user_name IS	'Valid user_name role names.';