-------------------------- uuid_not_empty------------------------DROP DOMAIN IF EXISTS domains.uuid_not_empty;CREATE DOMAIN domains.uuid_not_empty AS	uuid	NOT NULL	CHECK (value <> '20202020-2020-2020-2020-202020202020'	   AND value <> '00000000-0000-0000-0000-000000000000');COMMENT ON DOMAIN domains.uuid_not_empty IS	'The UUID must not be NULL, 00000000-0000-0000-0000-000000000000, or 20202020-2020-2020-2020-202020202020.';