DROP FUNCTION IF EXISTS tools.view_count_estimate (text, text);CREATE OR REPLACE FUNCTION tools.view_count_estimate (	schema_name text, 	view_name text) RETURNS BIGINT  AS $BODY$DECLARE   plan jsonb;   query text;BEGIN   EXECUTE      format(      E'select definition\n'      'from pg_views\n'      'where schemaname = %L\n'       '  and viewname   = %L',      schema_name,      view_name   ) INTO query;      EXECUTE    'EXPLAIN (FORMAT JSON) ' || query    INTO plan;    RETURN (plan->0->'Plan'->>'Plan Rows')::bigint;END;$BODY$	LANGUAGE plpgsqlSECURITY DEFINER;ALTER FUNCTION tools.view_count_estimate (text, text) OWNER TO user_bender;