DROP FUNCTION IF EXISTS api.date_get_fiscal_week (date);CREATE OR REPLACE FUNCTION api.date_get_fiscal_week (date_in date)RETURNS citextAS $BODY$DECLARE  first_day_of_year date = TO_DATE(EXTRACT(YEAR FROM date_in) || '-01-01', 'YYYY-MM-DD');  day_of_week_usa   int2 = EXTRACT(DOW FROM date_in) + 1; --  1-7 day, running from Sunday-Saturday. Same numbers as 4D.  go_back_days      int2 = day_of_week_usa - 1; -- Looks like we want a Sunday start?BEGIN  RAISE NOTICE 'first_day_of_year %', first_day_of_year;  RAISE NOTICE 'day_of_week_usa %',   day_of_week_usa;  RAISE NOTICE 'go_back_days %',   day_of_week_usa;END$BODY$LANGUAGE plpgsql STABLE;ALTER FUNCTION api.date_get_fiscal_week (date)     OWNER TO user_bender;