CREATE OR REPLACE FUNCTION api.convert_ib_timezone_name (tz_name_in citext)     RETURNS citext AS$BODY$-- IB uses custom names, convert these to standard tz names.SELECTCASE WHEN tz_name_in = 'Arizona'  THEN 'US/Arizona'::citext     WHEN tz_name_in = 'Central'  THEN 'US/Central'::citext     WHEN tz_name_in = 'Eastern'  THEN 'US/Eastern'::citext     WHEN tz_name_in = 'Mountain' THEN 'US/Mountain'::citext     WHEN tz_name_in = 'Pacific'  THEN 'US/Pacific'::citext     WHEN tz_name_in = 'Western'  THEN 'US/Pacific'::citext  -- IB uses 'Western' instead of 'Pacific'     ELSE 'UTC'::citextEND$BODY$  LANGUAGE SQL  IMMUTABLE;COMMENT ON FUNCTION api.convert_ib_timezone_name (citext) IS'Convert a custom IB time zone name, like ''Central'', to a standard name Postgres understands, like ''US/Central''.';ALTER FUNCTION api.convert_ib_timezone_name (citext)	OWNER TO user_bender;