DROP FUNCTION IF EXISTS api.domo_passthrough_expand (text, jsonb);CREATE FUNCTION api.domo_passthrough_expand(type_name text, packed_data jsonb)	RETURNS SETOF record AS$BODY$BEGIN    RETURN QUERY EXECUTE FORMAT   'select *      from jsonb_populate_recordset(null::%1$I, %2$I) as expanded_data;',           type_name,           packed_data;END;$BODY$LANGUAGE plpgsql STABLE;ALTER FUNCTION api.domo_passthrough_expand(text, jsonb) OWNER TO user_bender;