DROP FUNCTION IF EXISTS api.productivity_user (uuid, date, date, uuid);CREATE OR REPLACE FUNCTION api.productivity_user (    facility_id   uuid,    from_date     date,    to_date       date,    user_id       uuid)RETURNS TABLE (	  schema_name citext,	  view_name   citext) AS$BODY$-- DECLAREBEGIN-- The parameter types screen out most bad inputs, but it's still possible to screw up the start-end range. A few quick checks.IF from_date > to_date THEN  RAISE EXCEPTION USING     message = 'Invalid date range: ' || from_date::text || ' to ' || to_date::text,     hint = 'The from date is later than the to date.',     errcode = 'KC400'; -- Custom code, see the error_code table.END IF;END$BODY$LANGUAGE plpgsql STABLE;COMMENT ON FUNCTION api.productivity_user (uuid, date, date, uuid) IS'Pass in a facility ID, from date, to date, and a user ID to get back matching sent records.';ALTER function api.productivity_user (uuid, date, date, uuid)	OWNER TO user_bender;