--  select * from calendar_get_fiscal_month('2020-04-30'::date, '2020-01-01'::date) -- 5CREATE OR REPLACE FUNCTION ascendco.calendar_get_fiscal_month (	date_in date,	first_date_of_fiscal_year_in date)RETURNS integerAS $BODY$DECLARE    fiscal_week integer  := calendar_get_fiscal_week (date_in, first_date_of_fiscal_year_in);    fiscal_month integer := 0;BEGINSELECTCASE WHEN fiscal_week <  5 THEN  1	 WHEN fiscal_week <  9 THEN  2	 WHEN fiscal_week < 14 THEN  3	 WHEN fiscal_week < 18 THEN  4	 WHEN fiscal_week < 22 THEN  5	 WHEN fiscal_week < 27 THEN  6	 WHEN fiscal_week < 32 THEN  7	 WHEN fiscal_week < 35 THEN  8	 WHEN fiscal_week < 40 THEN  9	 WHEN fiscal_week < 44 THEN 10	 WHEN fiscal_week < 48 THEN 11	 ELSE                       12END INTO fiscal_month;RETURN fiscal_month;END;$BODY$	LANGUAGE plpgsql VOLATILE	COST 100;ALTER FUNCTION ascendco.calendar_get_fiscal_month (date, date)	OWNER TO user_bender;