-- select * from calendar_get_fiscal_week('2020-01-08'::date, '2020-01-01'::date) -- 2CREATE OR REPLACE FUNCTION ascendco.calendar_get_fiscal_week (	date_in date,	first_date_of_fiscal_year_in date)RETURNS integerAS $BODY$SELECT ((date_in - first_date_of_fiscal_year_in) / 7 ) +1$BODY$	LANGUAGE sql VOLATILE	COST 100;ALTER FUNCTION ascendco.calendar_get_fiscal_week (date, date)	OWNER TO user_bender;