CREATE OR REPLACE FUNCTION ascendco.facility_get_id (facility_name_in citext)     RETURNS uuidLANGUAGE sql STABLEAS$BODY$SELECT coalesce(    (SELECT id FROM ascendco.facility WHERE name_ = facility_name_in),    '00000000-0000-0000-0000-000000000000'::uuid);$BODY$;COMMENT ON FUNCTION ascendco.facility_get_id (citext) IS'Pass in a facility name and get back its ID, or a pseudo-null UUID.';ALTER FUNCTION ascendco.facility_get_id (citext)	OWNER TO user_bender;