CREATE OR REPLACE FUNCTION ascendco.facility_get_name (facility_id_in uuid)     RETURNS citextLANGUAGE sql STABLEAS$BODY$-- Need to put the select as an expression *inside* the COALESCE, or else it never fires on NULL. D'oh!-- Not the same in a straight select for Reasons That I Do Not Undestand.-- https://stackoverflow.com/questions/65973653/SELECT coalesce((SELECT name_ FROM ascendco.facility WHERE id = facility_id_in),'');$BODY$;COMMENT ON FUNCTION ascendco.facility_get_name (uuid) IS'Pass in a facility ID and get back its name.';ALTER FUNCTION ascendco.facility_get_name (uuid)	OWNER TO user_bender;