CREATE OR REPLACE FUNCTION ascendco.form_template_get_name (id_in uuid)     RETURNS citext AS$BODY$-- Need to put the select as an expression *inside* the COALESCE, or else it never fires on NULL. D'oh!-- Not the same in a straight select for Reasons That I Do Not Undestand.-- https://stackoverflow.com/questions/65973653/SELECT coalesce((SELECT name_ FROM ascendco.form_template WHERE id = id_in),'');$BODY$  LANGUAGE SQL;ALTER FUNCTION ascendco.form_template_get_name (uuid)	OWNER TO user_reporting;