CREATE OR REPLACE FUNCTION ascendco.hsys_get_facility_ids (hsys_id_in uuid)     RETURNS TABLE (facility_id uuid)LANGUAGE SQL STABLEAS$BODY$select id from facility where hsys_id = hsys_id_in;$BODY$;COMMENT ON FUNCTION ascendco.hsys_get_facility_ids (uuid) IS'Pass in a hsys ID and get back the IDs of all of its facilities.';ALTER FUNCTION ascendco.hsys_get_facility_ids (uuid)	OWNER TO user_bender;