CREATE OR REPLACE FUNCTION ascendco.hsys_get_id (hsys_name_in citext)     RETURNS uuidLANGUAGE sql STABLEAS$BODY$SELECT coalesce(    (SELECT id FROM ascendco.hsys WHERE name_ = hsys_name_in),    '00000000-0000-0000-0000-000000000000'::uuid);$BODY$;COMMENT ON FUNCTION ascendco.hsys_get_id (citext) IS'Pass in a hsys name and get back its ID, or a pseudo-null UUID.';ALTER FUNCTION ascendco.hsys_get_id (citext)	OWNER TO user_bender;