CREATE OR REPLACE FUNCTION ascendco.hsys_get_name (hsys_id_in uuid)     RETURNS citextLANGUAGE sql STABLEAS$BODY$-- Need to put the select as an expression *inside* the COALESCE, or else it never fires on NULL. D'oh!-- Not the same in a straight select for Reasons That I Do Not Undestand.-- https://stackoverflow.com/questions/65973653/SELECT coalesce((SELECT name_ FROM ascendco.hsys WHERE id = hsys_id_in),'');$BODY$;COMMENT ON FUNCTION ascendco.hsys_get_name (uuid) IS'Pass in a hsys ID and get back its name.';ALTER FUNCTION ascendco.hsys_get_name (uuid)	OWNER TO user_bender;