CREATE OR REPLACE FUNCTION ascendco.hsys_get_name_by_facility_id (facility_id_in uuid)     RETURNS citextLANGUAGE sql STABLEAS$BODY$SELECT coalesce(	(SELECT name_ FROM ascendco.hsys WHERE id = (select hsys_id  from facility where id = facility_id_in)),	'');$BODY$;COMMENT ON FUNCTION ascendco.hsys_get_name_by_facility_id (uuid) IS'Pass in a facility ID and get back the related hsys name.';ALTER FUNCTION ascendco.hsys_get_name_by_facility_id (uuid)	OWNER TO user_bender;