DROP FUNCTION IF EXISTS ascendco.push_target_add_if_missing(citext, citext, citext, citext);CREATE OR REPLACE FUNCTION ascendco.push_target_add_if_missing (     schema_name_in   citext,     target_name_in   citext,     unique_path_in   citext,     target_type_in   citext)RETURNS integer AS$BODY$DECLARE   id_out integer = 0;BEGIN-- Insert the value, if it's missing.INSERT INTO dba.push_target  (schema_name,    target_name,     unique_path,    target_type)                      VALUES (schema_name_in, target_name_in,  unique_path_in, target_type_in)                 ON CONFLICT DO NOTHING;-- The ID should be there, either historically or becase it was just added.     SELECT id       FROM dba.push_target      WHERE schema_name = schema_name_in        AND target_name = target_name_in        AND unique_path = unique_path_in        AND target_type = target_type_in       INTO id_out; return id_out as id;END$BODY$  LANGUAGE plpgsql;ALTER FUNCTION ascendco.push_target_add_if_missing(citext, citext, citext, citext)	OWNER TO user_bender;