CREATE OR REPLACE FUNCTION dba.debug_me (text) RETURNS boolean AS $BODY$BEGIN    RAISE NOTICE 'called as session_user=%, current_user=% for "%" ',         session_user, current_user, $1;    RETURN true;END;$BODY$ 	LANGUAGE plpgsql;COMMENT ON FUNCTION dba.debug_me (text) IS '/* https://www.cybertec-postgresql.com/en/postgresql-row-level-security-views-and-a-lot-of-magic/select debug_me(item.id::text),* from item*/';ALTER FUNCTION dba.debug_me (text) 	OWNER TO user_bender;