CREATE OR REPLACE FUNCTION dba.patch_log_insert(	patch_name_in     citext,	description_in    citext,	patch_hash_in     text)RETURNS patch_logAS $BODY$INSERT INTO patch_log (	           patch_name,	           description,	           patch_hash)       VALUES (patch_name_in,	           description_in,	           patch_hash_in)       RETURNING *$BODY$	LANGUAGE sql;ALTER FUNCTION dba.patch_log_insert (citext,citext,text)	OWNER TO user_bender;