CREATE OR REPLACE FUNCTION dba.column_default (qualified_name text, column_name text)  RETURNS text  AS $BODY$SELECT d.adsrc AS default_value -- A human-readable representation of the default value, already typed as text.FROM   pg_catalog.pg_attribute aLEFT   JOIN pg_catalog.pg_attrdef d ON (a.attrelid, a.attnum)                                     = (d.adrelid,  d.adnum)WHERE  NOT a.attisdropped   -- no dropped (dead) columnsAND    a.attnum > 0         -- no system columnsAND    a.attrelid = qualified_name::regclassAND    a.attname = column_name;$BODY$ 	LANGUAGE sql;COMMENT ON FUNCTION dba.column_default (text, text) IS '/* From Erwin Brandstetter:https://stackoverflow.com/questions/8146448/get-the-default-values-of-table-columns-in-postgres*/';ALTER FUNCTION dba.column_default (text, text) 	OWNER TO user_bender;