CREATE OR REPLACE FUNCTION dba.show_triggers ( )	RETURNS TABLE (		event_object_table text,		trigger_name text,		event_manipulation text,		action_statement text,		action_timing text )AS $BODY$  SELECT event_object_table,         trigger_name,         event_manipulation,         action_statement,         action_timing    FROM information_schema.triggersORDER BY event_object_table,         event_manipulation;$BODY$LANGUAGE sqlSTABLE;