DROP FUNCTION IF EXISTS     dba.push_log_recent (citext,int4);CREATE OR REPLACE FUNCTION  dba.push_log_recent (       table_name_in  citext,       limit_in       int4   DEFAULT 20)RETURNS TABLE (         table_name     citext,         minutes_old    int4,         records_count  int4,         server_name    citext,         push_log_id    citext) -- Eh? I reformat it for 4D's UUID style.LANGUAGE plpgsql STABLEAS $BODY$BEGINRETURN QUERY   select push_log.ib_table_name,          minutes_old (push_log.push_dts) as minutes_old,          push_log.records_count,          coalesce(data_file_info.server_name_, 'Unknown server with ID ' || format_uuid(push_log.data_file_id))::citext AS server_name,          format_uuid (push_log.id)::citext AS push_log_id     from push_logleft join data_file_info on data_file_info.id = push_log.data_file_id    where ib_table_name = table_name_in  order by push_dts desc   limit limit_in;END$BODY$;COMMENT ON FUNCTION dba.push_log_recent (citext,int4) IS'Pass in a table name and an optional imit to get the most recent information back from ascendco.push_log.';ALTER FUNCTION dba.push_log_recent (citext,int4)	OWNER TO user_bender;