CREATE OR REPLACE FUNCTION dba.show_owners()  RETURNS TABLE(  	schema_name name,   	object_name name,  	owner_name name,  	object_type text)   	AS $BODY$SELECT 	   nsp.nspname AS object_schema,       cls.relname AS object_name,        rol.rolname AS owner_name,        case cls.relkind         WHEN 'r' THEN 'TABLE'         WHEN 'v' THEN 'VIEW'         WHEN 'm' THEN 'MATERIALIZED_VIEW'         WHEN 'i' THEN 'INDEX'         WHEN 'S' THEN 'SEQUENCE'         WHEN 'c' THEN 'TYPE'         ELSE cls.relkind::text       END AS object_typeFROM pg_class cls  JOIN pg_roles rol ON rol.oid = cls.relowner  JOIN pg_namespace nsp ON nsp.oid = cls.relnamespaceWHERE nsp.nspname NOT IN ('information_schema', 'pg_catalog') AND			nsp.nspname NOT LIKE 'pg_toast%'ORDER BY  nsp.nspname, cls.relname;$BODY$  LANGUAGE sql STABLE  COST 100  ROWS 1000;  ALTER FUNCTION dba.show_owners()	OWNER TO user_bender;