CREATE OR REPLACE FUNCTION dba.show_schema_rights(s_name text)	RETURNS TABLE( 		schema_name text,  		user_name name,  		usage text,  		"create" text) 		AS $BODY$  	select s_name,				 u.usename,	         CASE WHEN has_schema_privilege(u.usename, s_name, 'usage') = TRUE then 'X' ELSE ' ' END AS usage,         CASE WHEN has_schema_privilege(u.usename, s_name, 'create') = TRUE then 'X' ELSE ' ' END AS create				  	from  pg_user u	order by u.usename;$BODY$	LANGUAGE sql	STABLE;ALTER FUNCTION dba.show_schema_rights(text) 	OWNER TO user_bender;