CREATE OR REPLACE FUNCTION dba.test_result_insert(	test_case_id_in    uuid,	test_passed_in     bool,	actual_outcome_in  test_outcome,	user_name_in       user_name,	patch_log_id_in    uuid   DEFAULT NULL,	actual_response_in citext DEFAULT '',	actual_error_in    citext DEFAULT '',	diagnostics_in     citext DEFAULT '',	summary_in         citext DEFAULT '',	results_json_in    jsonb  DEFAULT '{}'::jsonb)RETURNS test_resultAS $BODY$INSERT INTO test_result (	           test_case_id,	           actual_outcome,	           test_passed,	           patch_log_id,	           actual_response,	           actual_error,	           summary,	           diagnostics,	           results_json,	           user_name)       VALUES (test_case_id_in,	           actual_outcome_in,	           test_passed_in,	           patch_log_id_in,	           actual_response_in,	           actual_error_in,	           summary_in,	           diagnostics_in,	           results_json_in,	           user_name_in::user_name) -- user_name is a custom domain.		ON CONFLICT ON CONSTRAINT test_result_case_and_user_unique			DO UPDATE SET			   actual_outcome  = actual_outcome_in,               test_passed     = test_passed_in,               patch_log_id    = patch_log_id_in,               actual_response = actual_response_in,               actual_error    = actual_error_in,               summary         = summary_in,               diagnostics     = diagnostics_in,               results_json    = results_json_in,               user_name       = user_name_in::user_name -- user_name is a custom domain       RETURNING *$BODY$	LANGUAGE sql;ALTER FUNCTION dba.test_result_insert (uuid,bool,test_outcome,user_name,uuid,citext,citext,citext,citext,jsonb)	OWNER TO user_bender;