CREATE OR REPLACE FUNCTION dba.view_get_column_descriptions (view_name_in text)	RETURNS TABLE(		"Number" int4,		"Name" text,		"Type" text		)AS $BODY$SELECT ordinal_position as "Number",       column_name as "Name",       udt_name as "Type" FROM information_schema.columnsWHERE table_name = view_name_inORDER BY ordinal_position$BODY$	LANGUAGE sql	STABLE;ALTER FUNCTION dba.view_get_column_descriptions(text)	OWNER TO user_bender;