CREATE OR REPLACE FUNCTION dba.view_get_referenced_tables (view_name_in text)	RETURNS TABLE(		referenced_table text		)AS $BODY$  SELECT u.table_schema || '.' || u.table_name as referenced_table    FROM information_schema.view_table_usage u    JOIN information_schema.views v         ON  u.view_schema = v.table_schema         AND u.view_name = v.table_name   WHERE u.view_name = view_name_in::regclass::nameORDER BY u.view_schema,         u.view_name;$BODY$	LANGUAGE sql	STABLE;ALTER FUNCTION dba.view_get_referenced_tables(text)	OWNER TO user_bender;