-- Notice: This is a PROCEDURE, not a FUNCTION...no return value.-- You use CALL instead of SELECT to invoke a stored procedure.-- NOTE: It's all text below, no oid/regclass translation/validation, etc.-- This is a quick-and-dirty tool to make finding things simple for Welsh and William. -- DPACREATE OR REPLACE PROCEDURE dba.view_register (	schema_name_in     citext,	view_name_in       citext,	description_in     citext,	domo_dataset_in    citext DEFAULT '')AS $BODY$INSERT INTO dba.view_catalog (               schema_name,               view_name,               description,               domo_dataset)       VALUES (               schema_name_in,               view_name_in,               description_in,               domo_dataset_in)	ON CONFLICT ON CONSTRAINT view_catalog_id_pkey			DO UPDATE SET			   description   = description_in,			   domo_dataset  = domo_dataset_in$BODY$	LANGUAGE sql;ALTER PROCEDURE dba.view_register (citext, citext, citext, citext)	OWNER TO user_bender;-------------------------------------------------- Register view------------------------------------------------