DROP FUNCTION IF EXISTS tools.fuzzy_compare_jsonb (jsonb, jsonb, decimal_score, citext);CREATE OR REPLACE FUNCTION tools.fuzzy_compare_jsonb (     base_jsonb_in                        jsonb,     comparison_jsonb_in                  jsonb,     handicap_factor_in                   decimal_score DEFAULT 0.3,     monge_elkan_similarity_method_in     citext DEFAULT 'jarowinkler')RETURNS TABLE (	base_text                             citext,	comparison_text                       citext,	base_id                               citext,	comparison_id                         citext)AS $BODY$BEGINwithbase_expanded AS ( select *   from jsonb_to_recordset(base_jsonb_in)      AS unpacked (base_text citext, base_id citext) ),comparison_expanded AS ( select *   from jsonb_to_recordset(comparison_jsonb_in)      AS unpacked (comparison_text citext, comparison_id citext) )raise notice '%', base_expanded;raise notice '%', comparison_expanded;select * from base_expanded,comparison_expanded;END$BODY$LANGUAGE plpgsql;ALTER FUNCTION tools.fuzzy_compare_jsonb (jsonb, jsonb, decimal_score, citext)     OWNER TO user_bender;