CREATE OR REPLACE FUNCTION tools.array_remove_element(anyarray, int)  RETURNS anyarray LANGUAGE sql IMMUTABLE AS'SELECT $1[:$2-1] || $1[$2+1:]';COMMENT ON FUNCTION tools.array_remove_element(anyarray, int) IS 'From the ever-helpful Erwin Brandstetter, renamed for our schema and idiom.https://dba.stackexchange.com/questions/94639/delete-array-element-by-index';ALTER FUNCTION tools.array_remove_element (anyarray, int) 	OWNER TO user_bender;