CREATE OR REPLACE FUNCTION tools.count_rows (schema text, tablename text)	RETURNS pg_catalog.int4 	AS $BODY$DECLAREresult integer;query varchar;BEGINquery := 'SELECT count(1) FROM ' || schema || '.' || tablename;execute query into result;return result;END;$BODY$	LANGUAGE plpgsql 	VOLATILE	COST 100;	ALTER FUNCTION tools.count_rows (text, text) 	OWNER TO user_bender;	