CREATE OR REPLACE FUNCTION tools.count_rows_unqualified (tablename text)	RETURNS pg_catalog.int4 	AS $BODY$DECLAREresult integer;query varchar;-- Man, don't use this code anywhere live...it needs to be rewritten to use quot_ident(), etc. to avoid injection.BEGINquery := 'SELECT count(1) FROM ' || tablename;execute query into result;return result;END;$BODY$	LANGUAGE plpgsql 	VOLATILE	COST 100;	ALTER FUNCTION tools.count_rows_unqualified (text) 	OWNER TO user_bender;	