CREATE OR REPLACE FUNCTION tools.relkind_name (relkind text, out relkind_name text)  RETURNS textAS $BODY$SELECT CASE  WHEN relkind = 'r' THEN 'table'  WHEN relkind = 'i' THEN 'index'  WHEN relkind = 'S' THEN 'sequence'  WHEN relkind = 't' THEN 'TOAST table'  WHEN relkind = 'v' THEN 'view'  WHEN relkind = 'm' THEN 'materialized view'  WHEN relkind = 'c' THEN 'composite type'  WHEN relkind = 'f' THEN 'foreign table'  WHEN relkind = 'p' THEN 'partitioned table'  WHEN relkind = 'I' THEN 'partitioned index'  ELSE 'Unexpected relkind ' || relkindEND;$BODY$ 	LANGUAGE sql;ALTER FUNCTION tools.relkind_name (relkind text, out relkind_name text)	OWNER TO user_bender;