CREATE OR REPLACE FUNCTION tools.typtype_name (typtype text, out typtype_name text)	RETURNS text	AS $BODY$SELECT CASE  WHEN typtype = 'b' THEN 'base'  WHEN typtype = 'c' THEN 'composite type'  WHEN typtype = 'd' THEN 'domain'  WHEN typtype = 'e' THEN 'enum'  WHEN typtype = 'p' THEN 'pseudo-type'  WHEN typtype = 'r' THEN 'range'  ELSE 'Unexpected typtype ' || typtypeEND;$BODY$	LANGUAGE sql;ALTER FUNCTION tools.typtype_name (typtype text, out typtype_name text)	OWNER TO user_bender;