CREATE OR REPLACE FUNCTION tools.word_count (text_in citext)RETURNS intAS $BODY$DECLARE  text_in_words_array   text[];  word_count            int4;BEGINselect into text_in_words_array	string_to_array(text_in, ' ');word_count := array_length(text_in_words_array, 1);RETURN word_count;END$BODY$	LANGUAGE plpgsql;ALTER FUNCTION tools.word_count (citext)	OWNER TO user_bender;