CREATE OR REPLACE FUNCTION ascendco.trigger_function_log_deletion_count()	RETURNS triggerAS $BODY$DECLAREdeleted_count int := (select count(*) from deleted_rows);BEGINIF deleted_count > 0 THEN	   insert into ascendco.deletion_log				   (operation_name,				   schema_name,				   table_name,				   deleted_count)			 select TG_OP,					TG_TABLE_SCHEMA,					TG_TABLE_NAME,					deleted_count;END IF;	RETURN NULL;END;$BODY$	LANGUAGE plpgsql;ALTER FUNCTION ascendco.trigger_function_log_deletion_count ()	OWNER TO user_bender;