-- This view is selected in the 'trays_per_hour_pg' master UNION view, and is not pulled by Domo directly.-- View: passthrough.trays_per_hour_pg_v1DROP VIEW IF EXISTS passthrough.trays_per_hour_pg_v1 CASCADE;CREATE OR REPLACE VIEW passthrough.trays_per_hour_pg_v1 AS     SELECT            coalesce(hsys.name_,'') AS "HsysName",            coalesce(facility.name_,'') AS "FacilityName",            expanded_data."Assemble_DayName" AS "Assemble_DayName",            expanded_data."UserName" AS "UserName",            expanded_data."Assemble_Date" AS "Assemble_Date",            expanded_data."Assemble_Date"::timestamp AS "DateTime_Local",            expanded_data."HoursAssembly" AS "HoursAssembly",            expanded_data."InstPerHour" AS "InstPerHour",            expanded_data."TraysPerHour" AS "TraysPerHour",            expanded_data."TraysAssembled" AS "TraysAssembled",            expanded_data."Assemble_DayOfWeek" AS "Assemble_DayOfWeek",            expanded_data."InstAssembled" AS "InstAssembled"       FROM domo_passthrough  LEFT JOIN facility on (domo_passthrough.facility_id = facility.id)  LEFT JOIN hsys     on (domo_passthrough.hsys_id = hsys.id)CROSS JOIN jsonb_to_recordset(domo_passthrough.data) AS expanded_data (            "Assemble_DayName" citext,            "UserName" citext,            "Assemble_Date" date,            "HoursAssembly" float8,            "InstPerHour" float8,            "TraysPerHour" float8,            "TraysAssembled" int4,            "Assemble_DayOfWeek" int4,            "InstAssembled" int4)    WHERE dataset_name IN ('TraysPerHour_PG', 'TraysPerHour')      AND view_version = 1; -- Important! The view knows how to parse out JSONB data packed in this way.ALTER TABLE passthrough.trays_per_hour_pg_v1    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('passthrough','trays_per_hour_pg_v1','Pulled into ''trays_per_hour_pg'' master (UNION) view, not pulled directly. ','TraysPerHour_PG');