DROP PROCEDURE IF EXISTS ascendco.cleanup_fuzzy_text;CREATE PROCEDURE ascendco.cleanup_fuzzy_text ()AS $BODY$DELETE FROM ascendco.text_collection      WHERE added_dts < NOW() - INTERVAL '1 day'        AND retain = false;ANALYZE ascendco.text_collection;$BODY$    LANGUAGE SQL;COMMENT ON PROCEDURE ascendco.cleanup_fuzzy_text IS 'Clear out old, purgeable data in ascendco.text_collection.Note: This is a stored PROCEDURE, not a FUNCTION. CALL count_targets(), not SELECT count_targets().';ALTER PROCEDURE ascendco.cleanup_fuzzy_text ()   OWNER TO user_bender;