-- Log in as rds_super for this on Amazon.----------------------------------------------------------- public----------------------------------------------------------- Hard to secure, get rid of it.DROP SCHEMA IF EXISTS public;----------------------------------------------------------- ascendco---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS ascendco    AUTHORIZATION user_admin;COMMENT ON SCHEMA ascendco    IS 'This is our internal data.';----------------------------------------------------------- api---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS api    AUTHORIZATION group_admins;COMMENT ON SCHEMA api    IS 'This is a view-only, regulated access point for external clients.';----------------------------------------------------------- api_queries---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS api_queries    AUTHORIZATION group_admins;COMMENT ON SCHEMA api_queries    IS 'Query functions in their own API.';----------------------------------------------------------- analytics---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS analytics    AUTHORIZATION group_admins;COMMENT ON SCHEMA analytics    IS 'This is a storehouse for analytic and summary data.';----------------------------------------------------------- dba---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS dba    AUTHORIZATION group_admins;COMMENT ON SCHEMA dba    IS 'DBA-oriented scripts and resources.';----------------------------------------------------------- domains---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS domains    AUTHORIZATION group_admins;COMMENT ON SCHEMA domains    IS 'Custom DOMAIN definition for use by tables and functions.';----------------------------------------------------------- extensions---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS extensions    AUTHORIZATION group_admins;COMMENT ON SCHEMA extensions    IS 'This is a convenience schema to make extension loading and search_path resolution simpler to manage.';----------------------------------------------------------- etl---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS etl    AUTHORIZATION group_admins;COMMENT ON SCHEMA etl    IS 'ETL work zone for cleanups and such.';----------------------------------------------------------- event_triggers---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS event_triggers    AUTHORIZATION group_admins;COMMENT ON SCHEMA event_triggers    IS 'DDL change event triggers are stored here.';----------------------------------------------------------- passthrough---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS passthrough    AUTHORIZATION group_admins;COMMENT ON SCHEMA passthrough    IS 'This is a view-only, regulated access point for external clients drawing data extracted from packed records.';----------------------------------------------------------- text_analysis---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS text_analysis    AUTHORIZATION group_admins;COMMENT ON SCHEMA text_analysis    IS 'Text analysis tables and tools.';----------------------------------------------------------- tools---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS tools    AUTHORIZATION group_admins;COMMENT ON SCHEMA tools    IS 'General purpose scripts and resources.';----------------------------------------------------------- types---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS types    AUTHORIZATION group_admins;COMMENT ON SCHEMA tools    IS 'Custom type declarations for use by tables and functions.';----------------------------------------------------------- types_plus---------------------------------------------------------CREATE SCHEMA IF NOT EXISTS types_plus    AUTHORIZATION user_admin;COMMENT ON SCHEMA types_plus    IS 'Custom views and types for row insertion. This is where the Postgres part of our push plumbing is implemented';