-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.facility CASCADE;CREATE TABLE IF NOT EXISTS ascendco.facility (	id uuid NOT NULL DEFAULT NULL,	marked_for_deletion boolean NOT NULL DEFAULT false,	hsys_id uuid NOT NULL DEFAULT NULL,	name_ citext NOT NULL DEFAULT NULL,	tz_name citext NOT NULL DEFAULT NULL,	calendar_id uuid NOT NULL DEFAULT '21c27277-3aa4-4396-9293-c342cc693adc'::uuid);ALTER TABLE ascendco.facility	ADD CONSTRAINT facility_id_pkey    PRIMARY KEY (id);ALTER TABLE ascendco.facility	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- Nope. Postgres won't even bother with an index for searches on such a tiny table.-- Note: You always need an index for a unique constraint. PG defines that automatically for the PRIMARY.-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_facility_after_delete	AFTER DELETE	ON ascendco.facility	REFERENCING OLD TABLE AS deleted_rows	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_facility_before_truncate	BEFORE TRUNCATE	ON ascendco.facility	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();