-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.item CASCADE;CREATE TABLE IF NOT EXISTS ascendco.item (	id uuid NOT NULL DEFAULT NULL,	marked_for_deletion boolean NOT NULL DEFAULT false,	name_ citext NOT NULL DEFAULT NULL);ALTER TABLE ascendco.item	ADD CONSTRAINT item_id_pkey    PRIMARY KEY (id);    ALTER TABLE ascendco.item	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- Nope. The PRIMARYY gets us a B-tree automatically on id, and we're not searching direclty on name_ yet.-- Ah, and not deleting, so don't need the partial index on marked_for_deletion-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_item_after_delete	AFTER DELETE	ON ascendco.item	REFERENCING OLD TABLE AS deleted_rows 	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_item_before_truncate	BEFORE TRUNCATE	ON ascendco.item	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();