-- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.pgconfig_target CASCADE;CREATE TABLE IF NOT EXISTS ascendco.pgconfig_target (	id                int4         GENERATED ALWAYS AS IDENTITY,	schema_name       citext       NOT NULL DEFAULT NULL,	target_name       citext       NOT NULL DEFAULT NULL,	target_path       citext       NOT NULL DEFAULT NULL);ALTER TABLE ascendco.pgconfig_target	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes------------------------------------CREATE UNIQUE INDEX  pgconfig_target_unique_ix_btree     ON ascendco.pgconfig_target (schema_name, target_name, target_path);-------------------------------------- Add triggers-------------------------------------- None for now.-------------------------------------- Manual Permissions for now------------------------------------GRANT SELECT, INSERT, UPDATE, DELETE ON ascendco.pgconfig_target TO rds_super;GRANT SELECT, INSERT, UPDATE, DELETE ON ascendco.pgconfig_target TO rdsadmin;GRANT SELECT, INSERT, UPDATE, DELETE ON ascendco.pgconfig_target TO user_change_structure;GRANT SELECT, INSERT, UPDATE, DELETE ON ascendco.pgconfig_target TO user_cleanup;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_reporting;GRANT SELECT ON ascendco.pgconfig_target TO user_backup;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_iceberg;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_iceberg_remote;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_saws;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_sonar;GRANT SELECT, UPDATE ON ascendco.pgconfig_target TO user_leviathan;COMMIT;