-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE if exists ascendco.calendar CASCADE;CREATE TABLE ascendco.calendar(  id               uuid      NOT NULL DEFAULT NULL PRIMARY KEY, -- Hand-rolling UUIDs in advance, that's why this is set, not geneated.  calendar_name    citext    NOT NULL DEFAULT NULL UNIQUE,  week_starts_on   day_name  NOT NULL DEFAULT NULL, -- day_name is a custom domain, works as a type.  description      citext    NOT NULL DEFAULT '');ALTER TABLE ascendco.calendar	OWNER TO user_change_structure;-------------------------------------- Build indexes-------------------------------------- None needed. PG automatically builds a unique b-tree on the PRIMARY KEY id field.-------------------------------------- Add triggers-------------------------------------- None for this table. Cascading delete built into foreign keys from child tables.-------------------------------------- Manual Permissions for now------------------------------------GRANT SELECT, INSERT, UPDATE, DELETE ON ascendco.calendar TO rds_super;GRANT SELECT ON ascendco.calendar TO user_reporting;GRANT SELECT ON ascendco.calendar TO user_iceberg;GRANT SELECT ON ascendco.calendar TO user_iceberg_remote;GRANT SELECT ON ascendco.calendar TO user_saws;GRANT SELECT ON ascendco.calendar TO user_sonar;GRANT SELECT ON ascendco.calendar TO user_leviathan;GRANT SELECT ON ascendco.calendar TO user_change_structure;COMMIT;