-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.facility_location CASCADE;CREATE TABLE IF NOT EXISTS ascendco.facility_location (	id uuid NOT NULL DEFAULT NULL,	marked_for_deletion boolean NOT NULL DEFAULT false,	facility_id uuid NOT NULL DEFAULT NULL,	type_ citext NOT NULL DEFAULT NULL,	name_ citext NOT NULL DEFAULT NULL);ALTER TABLE ascendco.facility_location	ADD CONSTRAINT facility_location_id_pkey    PRIMARY KEY (id);ALTER TABLE ascendco.facility_location	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_facility_location_after_delete	AFTER DELETE	ON ascendco.facility_location	REFERENCING OLD TABLE AS deleted_rows	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_facility_location_before_truncate	BEFORE TRUNCATE	ON ascendco.facility_location	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();