-------------------------------------- Define table------------------------------------BEGIN;DROP TABLE IF EXISTS ascendco.item_type CASCADE;CREATE TABLE IF NOT EXISTS ascendco.item_type (	id uuid NOT NULL DEFAULT NULL,	marked_for_deletion boolean NOT NULL DEFAULT false,	name_ citext NOT NULL DEFAULT NULL);ALTER TABLE ascendco.item_type	ADD CONSTRAINT item_type_id_pkey    PRIMARY KEY (id);    ALTER TABLE ascendco.item_type	OWNER TO user_change_structure;COMMIT;-------------------------------------- Build indexes-------------------------------------- Nope. Postgres won't even bother with an index for searches on such a tiny table.-- Note: You always need an index for a unique constraint. PG defines that automatically for the PRIMARY.-------------------------------------- Add triggers------------------------------------CREATE TRIGGER trigger_item_type_after_delete	AFTER DELETE	ON ascendco.item_type	REFERENCING OLD TABLE AS deleted_rows 	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_deletion_count();CREATE TRIGGER trigger_item_type_before_truncate	BEFORE TRUNCATE	ON ascendco.item_type	FOR EACH STATEMENT	EXECUTE PROCEDURE ascendco.trigger_function_log_truncation_count();